using BinaryProvider # requires BinaryProvider 0.3.0 or later

# Parse some basic command-line arguments
const verbose = "--verbose" in ARGS
const prefix = Prefix(get([a for a in ARGS if a != "--verbose"], 1, joinpath(@__DIR__, "usr")))
const products = [
    LibraryProduct(prefix, "librelic_gmp_pbc_bls381", :librelic_gmp_pbc_bls381),
]

# Download binaries from hosted location
const version = "0.4.0-614-g0109b037-1"
const bin_prefix = "https://github.com/blenessy/RelicToolkitBuilder/releases/download/$version"

# Listing of files generated by BinaryBuilder:
const download_info = Dict(
    Linux(:i686, :glibc) => ("$bin_prefix/RelicToolkit.v$version.i686-linux-gnu.tar.gz", "78db3e8ce826c56e8ae3e7933a6c647d1dc92adc0a952e4bae387c0a43234a48"),
    Linux(:x86_64, :glibc) => ("$bin_prefix/RelicToolkit.v$version.x86_64-linux-gnu.tar.gz", "bc30a1851f0e5b8764c7245e8380eba4ee65c0773209ae5dabd740504fdcd1c4"),
    MacOS(:x86_64) => ("$bin_prefix/RelicToolkit.v$version.x86_64-apple-darwin14.tar.gz", "de6621e4cd748a5a6fd02bbd9b3214ac40b81209b18e72289ac08cd9f96a67f2"),
    Windows(:i686) => ("$bin_prefix/RelicToolkit.v$version.i686-w64-mingw32.tar.gz", "36b2978b1527e2b9575237ab5ad393a74ea34c9561bc4c56720e203eb0cde94e"),
    Windows(:x86_64) => ("$bin_prefix/RelicToolkit.v$version.x86_64-w64-mingw32.tar.gz", "62c1e36eaa44dd09258931e90e8e10d0804b9e4f892f0cc19474221d66df6687"),
)

# First, check to see if we're all satisfied
if any(!satisfied(p; verbose=verbose) for p in products)
    try
        # Download and install binaries
        url, tarball_hash = choose_download(download_info)
        install(url, tarball_hash; prefix=prefix, force=true, verbose=true)
    catch e
        if typeof(e) <: ArgumentError
            error("Your platform $(Sys.MACHINE) is not supported by this package!")
        else
            rethrow(e)
        end
    end
    # Finally, write out a deps.jl file
    write_deps_file(joinpath(@__DIR__, "deps.jl"), products)
end
